/*Table structure for table `tbl_commodity` */

DROP TABLE IF EXISTS `tbl_commodity`;

CREATE TABLE `tbl_commodity` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `shop` varchar(100) NOT NULL COMMENT '商家（旺旺号或店铺名称）',
  `shop_id` int(11) DEFAULT '0' COMMENT '商家ID',
  `commodity_id` varchar(50) NOT NULL COMMENT '商品ID',
  `sku` varchar(50) DEFAULT NULL COMMENT 'sku信息',
  `img` varchar(255) DEFAULT NULL COMMENT '宝贝图',
  `rule` text COMMENT '要求',
  `buyer_rule` text COMMENT '买手帐号要求',
  `remark` text COMMENT '备注',
  `create_time` datetime DEFAULT NULL COMMENT '添加时间',
  `uid` int(11) DEFAULT '0' COMMENT '用户ID',
  `statu` tinyint(1) DEFAULT '0' COMMENT '状态：0 待审核，1审核通过，2审核不通过',
  `platform_id` int(11) DEFAULT '0' COMMENT '平台ID',
  `platform` varchar(100) DEFAULT NULL COMMENT '平台',
  `credit` int(11) DEFAULT '0' COMMENT '买手信用',
  `trade_num` int(11) DEFAULT '0' COMMENT '买手交易数',
  PRIMARY KEY (`id`),
  FULLTEXT KEY `shop_id_index` (`shop`),
  FULLTEXT KEY `commodity_id_index` (`commodity_id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

